mapper.select:
SELECT u.id,u.nickname,u.email,u.roles,u.loginIp,u.loginTime,u.loginarea,u.createdIp,u.createdTime,u.createdarea,p.truename,p.gender,p.idcard,p.mobile,p.company,p.job,p.birthday,p.weibo,p.weixin,p.site,p.signature,p.about,p.iam,p.isQQPublic,p.isWeiboPublic,p.isWeixinPublic
FROM `user` u,user_profile p
WHERE u.id = p.id and u.id = #{userId}
